import FindCarForm from "../components/UI/FindCarForm";
import AboutSection from "../components/UI/AboutSection";


const Home = () => {


    var sBrowser, sUsrAg = navigator.userAgent;

    useEffect(() => {



        if(sUsrAg.indexOf("Chrome") > -1) {
  document.body.style.zoom = "100%" 

        }


        else 

        { document.body.style.zoom = "100%"  }


    });


  return (
    <Helmet title="Home">
      {/* ============= hero section =========== */}
