                </div>
              </Col>

              <Col lg="6" md="10" sm="12">
                <FindCarForm />
              </Col>
            </Row>
          </Container>
        </div>
      </section>
      {/* =========== about section ================ */}
      <AboutSection />
      {/* ========== services section ============ */}





      {/* =============== blog section =========== */}

    </Helmet>
  );
};

export default Home;
